mapper.sql:
SELECT b.bindhost_id FROM audit_user_info_groups AS a JOIN audit_hostgroup_bind_hosts AS b ON a.group_id = b.hostgroup_id
AND a.user_info_id = #{userId}
AND a.group_id = #{groupId}

mapper.select:
select
  name
from audit_idc where id = #{id}

SELECT b.id as id, c.host_name as host_name, c.ip_address as ip_address, c.idc_id as idc_id, c.`port` as port, c.enabled as enabled, d.username as username, d.auth_type as auth_type, e.name as idc FROM audit_user_info_groups AS f JOIN audit_hostgroup_bind_hosts AS a on f.group_id = a.hostgroup_id JOIN audit_bindhost AS b ON a.bindhost_id = b.id JOIN audit_host AS c on b.host_id = c.id JOIN audit_hostuser AS d on b.host_user_id = d.id join audit_idc as e on e.id = c.idc_id
AND f.user_info_id = #{userId}
AND f.group_id = #{groupId}
AND c.host_name like concat('%', #{hostName, jdbcType=VARCHAR}, '%')
AND e.name like concat('%', #{idc, jdbcType=VARCHAR}, '%')
